select 

	 IFNULL(sum(transAmt), 0.00) AS money from bps_gdx_epp a 


	 IFNULL(sum(acceptAmt), 0.00) AS money from  bps_gdx_zdfq a 


	 IFNULL(sum(amount), 0.00) AS money from  bps_gdx_eppc a 


	 IFNULL(sum(applyAmt), 0.00) AS money  from bps_gdx_deczj a 



	 where mainyw='EPP' 


	 where mainyw='账单分期' 


	 where mainyw='大额EPPC' 


	 where mainyw='EPPC' AND pettycash!='新发卡备用金' 


	 where mainyw='EPPC' AND pettycash='新发卡备用金' 


	 left join md_success b on a.md_id = b.md_id where a.mainyw='EPPC' and b.pettycash != '新发卡备用金' 


	 left join md_success b on a.md_id = b.md_id where a.mainyw='EPPC' and b.pettycash = '新发卡备用金' 


 and gdx_exzx=#userName# AND str_to_date(gdx_extime,'%Y-%m-%d %H:%i:%s') >=#beginTime# AND str_to_date(gdx_extime,'%Y-%m-%d %H:%i:%s') <= #endTime# AND forceTransfer !=1 

	 and  bills='3';


	 and  bills='6';


	 and  bills='12';


	 and  bills='18';


	 and  bills='24';


	 and  bills='36';

select 

	 IFNULL(SUM(transAmt), 0.00) as money from bps_gdx_epp a 


	 IFNULL(sum(acceptAmt), 0.00) as money from   bps_gdx_zdfq a 


	 IFNULL(sum(amount), 0.00) as money from  bps_gdx_eppc a 


	 IFNULL(sum(applyAmt), 0.00) as money from bps_gdx_deczj a 



	 where mainyw='EPP' 


	 where mainyw='账单分期' 


	 where mainyw='大额EPPC' 


	 where mainyw='EPPC' AND pettycash!='新发卡备用金' 


	 where mainyw='EPPC' AND pettycash='新发卡备用金' 


	 left join md_success b on a.md_id = b.md_id where a.mainyw='EPPC' and b.pettycash != '新发卡备用金' 


	 left join md_success b on a.md_id = b.md_id where a.mainyw='EPPC' and b.pettycash = '新发卡备用金' 


  and gdx_exzx=#userName# AND Update_t >=#beginTime# AND Update_t <= #endTime# AND forceTransfer =1  AND approve_rst = '成功' and status_Approved = '已批核' 

	 and  bills='3';


	 and  bills='6';


	 and  bills='12';


	 and  bills='18';


	 and  bills='24';


	 and  bills='36';

select 

	 COUNT(DISTINCT md_id) AS amount from bps_gdx_autofq a WHERE installment='YY01' AND czlx != 'C' AND gdx_exzx =#userName# and str_to_date(a.gdx_extime,'%Y-%m-%d %H:%i:%s')>=#beginTime# AND str_to_date(a.gdx_extime,'%Y-%m-%d %H:%i:%s') <= #endTime# AND mainyw='EPP';


	 COUNT(DISTINCT md_id) AS amount from bps_gdx_autofq a WHERE installment='YY01' AND czlx != 'C' AND gdx_exzx =#userName# and str_to_date(a.gdx_extime,'%Y-%m-%d %H:%i:%s')>=#beginTime# AND str_to_date(a.gdx_extime,'%Y-%m-%d %H:%i:%s') <= #endTime# AND mainyw='账单分期';


	 COUNT(DISTINCT md_id) AS amount from bps_gdx_autofq a WHERE installment='YY01' AND czlx != 'C' AND gdx_exzx =#userName# and str_to_date(a.gdx_extime,'%Y-%m-%d %H:%i:%s')>=#beginTime# AND str_to_date(a.gdx_extime,'%Y-%m-%d %H:%i:%s') <= #endTime# AND mainyw='大额EPPC';


	 COUNT(a.md_id) AS amount from bps_gdx_autofq a LEFT JOIN md_success b on a.md_id=b.md_id WHERE a.installment='YY01' AND a.czlx != 'C' AND a.gdx_exzx =#userName# and str_to_date(a.gdx_extime,'%Y-%m-%d %H:%i:%s')>=#beginTime# AND str_to_date(a.gdx_extime,'%Y-%m-%d %H:%i:%s') <= #endTime# AND a.mainyw='EPPC' AND b.pettycash!='新发卡备用金';


	 COUNT(a.md_id) AS amount from bps_gdx_autofq a LEFT JOIN md_success b on a.md_id=b.md_id WHERE a.installment='YY01' AND a.czlx != 'C' AND a.gdx_exzx =#userName# and str_to_date(a.gdx_extime,'%Y-%m-%d %H:%i:%s')>=#beginTime# AND str_to_date(a.gdx_extime,'%Y-%m-%d %H:%i:%s') <= #endTime# AND a.mainyw='EPPC' AND b.pettycash!='新发卡备用金';

SELECT COUNT(DISTINCT a.md_id) AS amount FROM insurance_reg a 

	 WHERE yw_id=1 AND user_name=#userName# AND if_insurance='1' AND save_time >=#beginTime# AND save_time <= #endTime#;


	 WHERE yw_id=10 AND user_name=#userName# AND if_insurance='1' AND save_time >=#beginTime# AND save_time <= #endTime#;


	 WHERE yw_id=11 AND user_name=#userName# AND if_insurance='1' AND save_time >=#beginTime# AND save_time <= #endTime#;


	 LEFT JOIN md_success b on a.md_id=b.md_id  WHERE a.yw_id=2 AND a.user_name=#userName# AND a.if_insurance='1' AND a.save_time >=#beginTime# AND a.save_time <= #endTime# AND b.pettycash!='新发卡备用金';


	 LEFT JOIN md_success b on a.md_id=b.md_id  WHERE a.yw_id=2 AND a.user_name=#userName# AND a.if_insurance='1' AND a.save_time >=#beginTime# AND a.save_time <= #endTime# AND b.pettycash='新发卡备用金';

SELECT COUNT(DISTINCT a.md_id) AS amount FROM insurance_reg a 

	 WHERE yw_id=1 AND user_name=#userName# AND rec_card='1' AND str_to_date(a.cur_time,'%Y-%m-%d %H:%i:%s') >=#beginTime# AND str_to_date(a.cur_time,'%Y-%m-%d %H:%i:%s') <= #endTime#;


	 WHERE yw_id=10 AND user_name=#userName# AND rec_card='1' AND str_to_date(a.cur_time,'%Y-%m-%d %H:%i:%s') >=#beginTime# AND str_to_date(a.cur_time,'%Y-%m-%d %H:%i:%s') <= #endTime#;


	 WHERE yw_id=11 AND user_name=#userName# AND rec_card='1' AND str_to_date(a.cur_time,'%Y-%m-%d %H:%i:%s') >=#beginTime# AND str_to_date(a.cur_time,'%Y-%m-%d %H:%i:%s') <= #endTime#;


	 LEFT JOIN md_success b on a.md_id=b.md_id  WHERE a.yw_id=2 AND a.user_name=#userName# AND a.rec_card='1' AND str_to_date(a.cur_time,'%Y-%m-%d %H:%i:%s') >=#beginTime# AND str_to_date(a.cur_time,'%Y-%m-%d %H:%i:%s') <= #endTime# AND b.pettycash!='新发卡备用金';


	 LEFT JOIN md_success b on a.md_id=b.md_id  WHERE a.yw_id=2 AND a.user_name=#userName# AND a.rec_card='1' AND str_to_date(a.cur_time,'%Y-%m-%d %H:%i:%s') >=#beginTime# AND str_to_date(a.cur_time,'%Y-%m-%d %H:%i:%s') <= #endTime# AND b.pettycash='新发卡备用金';

