sqlMap.select:
SELECT COUNT(DISTINCT a.md_id) AS amount from bps_rw_history a 

	 WHERE rw_ctime >=CONCAT(CURDATE(),' 00:00:00') AND user_name= #userName# AND yw_type ='EPP';


	 WHERE rw_ctime >=CONCAT(CURDATE(),' 00:00:00') AND user_name= #userName# AND yw_type ='账单分期';


	 WHERE rw_ctime >=CONCAT(CURDATE(),' 00:00:00') AND user_name= #userName# AND yw_type ='大额EPPC';


	 LEFT JOIN mdx_eppc b on a.dr_key =b.dr_key WHERE a.rw_ctime >=CONCAT(CURDATE(),' 00:00:00') AND a.yw_type ='EPPC' AND b.Data_type!='新发卡备用金' and a.user_name= #userName#;


	 LEFT JOIN mdx_eppc b on a.dr_key =b.dr_key WHERE a.rw_ctime >=CONCAT(CURDATE(),' 00:00:00') AND a.yw_type ='EPPC' AND b.Data_type='新发卡备用金'  and a.user_name= #userName#;

SELECT COUNT(DISTINCT a.md_id) AS amount FROM `bps_rw_history` a 

	 where user_name = #userName# AND yw_type='EPP' AND gd_type='EPP工单' AND Rw_Status_sn='A' AND rw_ctime >=CONCAT(CURDATE(),' 00:00:00'); 


	 where user_name = #userName# AND yw_type='账单分期' AND gd_type='账单分期工单' AND Rw_Status_sn='A' AND rw_ctime >=CONCAT(CURDATE(),' 00:00:00'); 


	 where user_name = #userName# AND yw_type='大额EPPC' AND gd_type='大额EPPC工单' AND Rw_Status_sn='A' AND rw_ctime >=CONCAT(CURDATE(),' 00:00:00'); 


	 left join mdx_eppc b on a.dr_key=b.dr_key where a.rw_ctime >=CONCAT(CURDATE(),' 00:00:00') AND a.Rw_Status_sn='A' and a.yw_type='EPPC' AND a.gd_type='EPPC工单' AND a.user_name=#userName# AND b.Data_type!= '新发卡备用金';


	 left join mdx_eppc b on a.dr_key=b.dr_key where a.rw_ctime >=CONCAT(CURDATE(),' 00:00:00') AND a.Rw_Status_sn='A' and a.yw_type='EPPC' AND a.gd_type='EPPC工单' AND a.user_name=#userName# AND b.Data_type= '新发卡备用金';

select 

	IFNULL(sum(transAmt), 0.00) AS money from bps_gdx_epp where gdx_exzx=#userName# AND mainyw='EPP' AND gdx_extime >=CONCAT(CURDATE(),' 00:00:00') AND forceTransfer !=1; 


	IFNULL(sum(acceptAmt), 0.00) AS money from  bps_gdx_zdfq where  gdx_exzx=#userName# AND mainyw='账单分期' AND gdx_extime >=CONCAT(CURDATE(),' 00:00:00') AND forceTransfer !=1;


	IFNULL(sum(applyAmt), 0.00) AS money  from bps_gdx_deczj where gdx_exzx=#userName# AND mainyw='大额EPPC' AND gdx_extime >=CONCAT(CURDATE(),' 00:00:00') AND forceTransfer !=1;


	IFNULL(sum(a.amount), 0.00) AS money from  bps_gdx_eppc a LEFT JOIN mdx_eppc b ON a.mdx_eppcid=b.mdx_eppcid WHERE b.Data_type!='新发卡备用金' AND a.gdx_exzx=#userName# AND a.gdx_extime >=CONCAT(CURDATE(),' 00:00:00') AND a.mainyw='EPPC' AND a.forceTransfer !=1;


	IFNULL(sum(a.amount), 0.00) AS money from  bps_gdx_eppc a LEFT JOIN mdx_eppc b ON a.mdx_eppcid=b.mdx_eppcid WHERE b.Data_type='新发卡备用金' AND a.gdx_exzx=#userName# AND a.gdx_extime >=CONCAT(CURDATE(),' 00:00:00') AND a.mainyw='EPPC' AND a.forceTransfer !=1;

select 

	IFNULL(SUM(transAmt), 0.00) AS money from   bps_gdx_epp where gdx_exzx=#userName# AND mainyw='EPP' AND Update_t >=CONCAT(date_add(CURDATE(),interval -1 day),' 23:30:00') AND Update_t <= CONCAT(CURDATE(),' 07:00:00') AND forceTransfer =1 AND approve_rst = '成功' and status_Approved = '已批核';


	IFNULL(sum(acceptAmt), 0.00) AS money  from   bps_gdx_zdfq where  gdx_exzx=#userName# AND mainyw='账单分期' AND Update_t >=CONCAT(date_add(CURDATE(),interval -1 day),' 23:30:00') AND Update_t <= CONCAT(CURDATE(),' 07:00:00') AND forceTransfer =1 AND approve_rst = '成功' and status_Approved = '已批核';


	IFNULL(sum(applyAmt), 0.00) AS money from  bps_gdx_deczj where gdx_exzx=#userName# AND mainyw='大额EPPC' AND Update_t >=CONCAT(date_add(CURDATE(),interval -1 day),' 23:30:00') AND Update_t <= CONCAT(CURDATE(),' 07:00:00') AND forceTransfer =1 AND approve_rst = '成功' and status_Approved = '已批核';


	IFNULL(sum(a.amount), 0.00) AS money from  bps_gdx_eppc a LEFT JOIN mdx_eppc b ON a.mdx_eppcid=b.mdx_eppcid WHERE b.Data_type!='新发卡备用金' AND a.gdx_exzx=#userName# AND a.Update_t >=CONCAT(date_add(CURDATE(),interval -1 day),' 23:30:00') AND Update_t <= CONCAT(CURDATE(),' 07:00:00') AND a.mainyw='EPPC' AND a.forceTransfer =1 AND a.approve_rst = '成功' and a.status_Approved = '已批核';


	IFNULL(sum(a.amount), 0.00) AS money from  bps_gdx_eppc a LEFT JOIN mdx_eppc b ON a.mdx_eppcid=b.mdx_eppcid WHERE b.Data_type='新发卡备用金' AND a.gdx_exzx=#userName# AND a.Update_t >=CONCAT(date_add(CURDATE(),interval -1 day),' 23:30:00') AND Update_t <= CONCAT(CURDATE(),' 07:00:00') AND a.mainyw='EPPC' AND a.forceTransfer =1 AND a.approve_rst = '成功' and a.status_Approved = '已批核';

SELECT DISTINCT a.callid  from bps_rw_history a 

	 WHERE user_name= #userName# and rw_ctime>=CONCAT(CURDATE(),' 00:00:00') AND callid != '' and yw_type='EPP';


	 WHERE user_name= #userName# and rw_ctime>=CONCAT(CURDATE(),' 00:00:00') AND callid != '' and yw_type='账单分期';


	 WHERE user_name= #userName# and rw_ctime>=CONCAT(CURDATE(),' 00:00:00') AND callid != '' and yw_type='大额EPPC'; 


	 LEFT JOIN mdx_eppc b on a.dr_key =b.dr_key WHERE a.rw_ctime>=CONCAT(CURDATE(),' 00:00:00') AND a.yw_type ='EPPC' AND b.Data_type!='新发卡备用金' and a.user_name= #userName# ;


	 LEFT JOIN mdx_eppc b on a.dr_key =b.dr_key WHERE a.rw_ctime>=CONCAT(CURDATE(),' 00:00:00') AND a.yw_type ='EPPC' AND b.Data_type!='新发卡备用金' and a.user_name= #userName# ;
