SELECT COUNT(DISTINCT a.md_id) AS amount from bps_rw_history a 

	 WHERE rw_ctime > #beginTime# AND rw_ctime <= #endTime# AND a.callid !=''AND Rw_Status_sn='A' and user_name= #userName# AND yw_type ='EPP';


	 WHERE rw_ctime > #beginTime# AND rw_ctime <= #endTime# AND a.callid !=''AND Rw_Status_sn='A' and user_name= #userName# AND yw_type ='账单分期';


	 WHERE rw_ctime > #beginTime# AND rw_ctime <= #endTime# AND a.callid !=''AND Rw_Status_sn='A' and user_name= #userName# AND yw_type ='大额EPPC';


	 LEFT JOIN mdx_eppc b on a.dr_key =b.dr_key WHERE a.rw_ctime > #beginTime# AND a.callid !=''AND a.Rw_Status_sn='A' AND a.rw_ctime <=#endTime# AND a.yw_type ='EPPC' AND b.Data_type!='新发卡备用金' and a.user_name= #userName#;


	 LEFT JOIN mdx_eppc b on a.dr_key =b.dr_key WHERE a.rw_ctime > #beginTime# AND a.callid !=''AND a.Rw_Status_sn='A' AND a.rw_ctime <=#endTime# AND a.yw_type ='EPPC' AND b.Data_type='新发卡备用金' and a.user_name= #userName# ;

SELECT COUNT(DISTINCT a.md_id) AS amount from bps_rw_history a 

	 WHERE rw_ctime > #beginTime# AND rw_ctime <= #endTime# AND a.callid !='' and user_name= #userName# AND yw_type ='EPP';


	 WHERE rw_ctime > #beginTime# AND rw_ctime <= #endTime# AND a.callid !='' and user_name= #userName# AND yw_type ='账单分期';


	 WHERE rw_ctime > #beginTime# AND rw_ctime <= #endTime# AND a.callid !='' and user_name= #userName# AND yw_type ='大额EPPC';


	 LEFT JOIN mdx_eppc b on a.dr_key =b.dr_key WHERE a.rw_ctime > #beginTime# AND a.rw_ctime <= #endTime# AND a.yw_type ='EPPC' AND b.Data_type!='新发卡备用金' and a.user_name= #userName# ;


	 LEFT JOIN mdx_eppc b on a.dr_key =b.dr_key WHERE a.rw_ctime > #beginTime# AND a.rw_ctime <= #endTime# AND a.yw_type ='EPPC' AND b.Data_type='新发卡备用金'  and a.user_name= #userName#;

SELECT DISTINCT a.callid  from bps_rw_history a

	 WHERE user_name= #userName#  and a.rw_ctime >= #beginTime# and a.rw_ctime <=#endTime# AND a.callid != '' and yw_type='EPP';


	 WHERE user_name= #userName#  and a.rw_ctime >= #beginTime# and a.rw_ctime <=#endTime# AND a.callid != '' and yw_type='账单分期';


	 WHERE user_name= #userName#  and a.rw_ctime >= #beginTime# and a.rw_ctime <=#endTime# AND a.callid != '' and yw_type='大额EPPC';


	 LEFT JOIN mdx_eppc b on a.dr_key =b.dr_key WHERE a.rw_ctime > #beginTime# AND a.rw_ctime <= #endTime# AND a.yw_type ='EPPC' AND b.Data_type!='新发卡备用金' and a.user_name= #userName#;


	 LEFT JOIN mdx_eppc b on a.dr_key =b.dr_key WHERE a.rw_ctime > #beginTime# AND a.rw_ctime <= #endTime# AND a.yw_type ='EPPC'  AND b.Data_type='新发卡备用金' and a.user_name= #userName#;

SELECT DISTINCT a.md_id AS id from bps_rw_history a 

	 WHERE user_name= #userName# AND Rw_Status_sn = 'A' and a.rw_ctime >=#beginTime# and a.rw_ctime <=#endTime# and yw_type='EPP';


	 WHERE user_name= #userName# AND Rw_Status_sn = 'A' and a.rw_ctime >=#beginTime# and a.rw_ctime <=#endTime# and yw_type='账单分期';


	 WHERE user_name= #userName# AND Rw_Status_sn = 'A' and a.rw_ctime >=#beginTime# and a.rw_ctime <=#endTime# and yw_type='大额EPPC';


	 LEFT JOIN mdx_eppc b on a.dr_key =b.dr_key WHERE a.rw_ctime >#beginTime# AND a.rw_ctime <=#endTime# AND a.yw_type ='EPPC' AND a.Rw_Status_sn = 'A' AND b.Data_type!='新发卡备用金' and a.user_name= #userName#;


	 LEFT JOIN mdx_eppc b on a.dr_key =b.dr_key WHERE a.rw_ctime >#beginTime# AND a.rw_ctime <=#endTime# AND a.yw_type ='EPPC' AND a.Rw_Status_sn = 'A' AND b.Data_type='新发卡备用金' and a.user_name= #userName#;
