sqlMap.select:
SELECT 

	IFNULL(sum(transAmt*bills*feeRatio/100), 0.00) AS money FROM bps_gdx_epp WHERE forceTransfer !='1' 


	IFNULL(sum(acceptAmt*bills*feeRatio1/100), 0.00) AS money FROM bps_gdx_zdfq WHERE forceTransfer !='1' 


	IFNULL(sum(applyAmt*bills*Ave_RATE/100), 0.00) AS money FROM bps_gdx_deczj WHERE forceTransfer !='1' 


	IFNULL(sum(amount*bills*Ave_RATE/100), 0.00) AS money FROM bps_gdx_eppc WHERE forceTransfer !='1' and pettycash!='新发卡备用金'  


	IFNULL(sum(amount*bills*Ave_RATE/100), 0.00) AS money FROM bps_gdx_eppc WHERE forceTransfer !='1' and pettycash='新发卡备用金'  


  and gdx_exzx=#userName# and gdx_extime >=CONCAT(CURDATE(),' 00:00:00') AND gdx_extime <= CONCAT(CURDATE(),' 23:59:59') 


	 and  bills='3';


	 and  bills='6';


	 and  bills='12';


	 and  bills='18';


	 and  bills='24';


	 and  bills='36';

SELECT 

	 IFNULL(sum(transAmt*bills*feeRatio/100), 0.00) AS money FROM bps_gdx_epp WHERE forceTransfer =1 


	 IFNULL(sum(acceptAmt*bills*feeRatio1/100), 0.00) AS money FROM bps_gdx_zdfq WHERE forceTransfer =1 


	 IFNULL(sum(applyAmt*bills*Ave_RATE/100), 0.00) AS money FROM bps_gdx_deczj WHERE forceTransfer =1 


	 IFNULL(sum(amount*bills*Ave_RATE/100), 0.00) AS money FROM bps_gdx_eppc WHERE forceTransfer =1 and pettycash!='新发卡备用金' 


	 IFNULL(sum(amount*bills*Ave_RATE/100), 0.00) AS money FROM bps_gdx_eppc WHERE forceTransfer =1 and pettycash='新发卡备用金' 


  and gdx_exzx= #userName# and Update_t >= CONCAT(date_add(CURDATE(),interval -1 day),' 23:30:00') AND Update_t <= CONCAT(CURDATE(),' 07:00:00') AND approve_rst = '成功' and status_Approved = '已批核' 


	 and  bills='3';


	 and  bills='6';


	 and  bills='12';


	 and  bills='18';


	 and  bills='24';


	 and  bills='36';

select 

	 IFNULL(sum(transAmt), 0.00) AS money from bps_gdx_epp where mainyw='EPP' 


	 IFNULL(sum(acceptAmt), 0.00) AS money from  bps_gdx_zdfq where mainyw='账单分期' 


	 IFNULL(sum(applyAmt), 0.00) AS money from bps_gdx_deczj where mainyw='大额EPPC' 


	 IFNULL(sum(amount), 0.00) AS money bps_gdx_eppc where mainyw='EPPC' and pettycash!='新发卡备用金' 


	 IFNULL(sum(amount), 0.00) AS money bps_gdx_eppc where mainyw='EPPC' and pettycash='新发卡备用金' 

  and forceTransfer !='1' AND gdx_exzx=#userName# AND gdx_extime >=CONCAT(CURDATE(),' 00:00:00') AND gdx_extime <= CONCAT(CURDATE(),' 23:59:59')  


	 and  bills='18';


	 and  bills='24';


	 and  bills='36';

select 

	 IFNULL(sum(transAmt), 0.00) AS money from bps_gdx_epp where mainyw='EPP' 


	 IFNULL(sum(acceptAmt), 0.00) AS money from  bps_gdx_zdfq where mainyw='账单分期' 


	 IFNULL(sum(applyAmt), 0.00) AS money from bps_gdx_deczj where mainyw='大额EPPC' 


	 IFNULL(sum(amount), 0.00) AS money bps_gdx_eppc where mainyw='EPPC' and pettycash!='新发卡备用金' 


	 IFNULL(sum(amount), 0.00) AS money bps_gdx_eppc where mainyw='EPPC' and pettycash='新发卡备用金' 

   and forceTransfer ='1' and Update_t >= CONCAT(date_add(CURDATE(),interval -1 day),' 23:30:00') AND Update_t <= CONCAT(CURDATE(),' 07:00:00') AND approve_rst = '成功' and status_Approved = '已批核' 

	 and  bills='18';


	 and  bills='24';


	 and  bills='36';
