sqlMap.select:
select count(DISTINCT a.md_id) AS amount from bps_rw_history a LEFT JOIN 

	internet_ipcc.t_toll_universal b on a.callid=b.invokeId where a.user_name = #userName# AND a.yw_type='EPP' AND a.callid!='' AND a.rw_ctime >#beginTime# AND a.rw_ctime <=#endTime# AND b.invokeId!='';


	internet_ipcc.t_toll_universal b on a.callid=b.invokeId where a.user_name = #userName# AND a.yw_type='账单分期' AND a.callid!='' AND a.rw_ctime >#beginTime# AND a.rw_ctime <=#endTime# AND b.invokeId!='';


	internet_ipcc.t_toll_universal b on a.callid=b.invokeId where a.user_name = #userName# AND a.yw_type='大额EPPC' AND a.callid!='' AND a.rw_ctime >#beginTime# AND a.rw_ctime <=#endTime# AND b.invokeId!='';


	mdx_eppc b ON a.dr_key=b.dr_key LEFT JOIN internet_ipcc.t_toll_universal c on a.callid=c.invokeId where  a.user_name = #userName# AND a.yw_type='EPPC' AND a.callid!='' AND a.rw_ctime >#beginTime# AND a.rw_ctime <=#endTime# AND b.Data_type !='新发卡备用金'  AND c.invokeId!='';


	mdx_eppc b ON a.dr_key=b.dr_key LEFT JOIN internet_ipcc.t_toll_universal c on a.callid=c.invokeId where  a.user_name = #userName# AND a.yw_type='EPPC' AND a.callid!='' AND a.rw_ctime >#beginTime# AND a.rw_ctime <=#endTime# AND b.Data_type ='新发卡备用金'  AND c.invokeId!='';

select count(a.md_id) AS amount from  bps_rw_history a 

	where  user_name = #userName# AND yw_type='EPP' AND gd_type='EPP工单' AND Rw_Status_sn='A' AND a.rw_ctime >#beginTime# AND a.rw_ctime <=#endTime#; 


	where  user_name = #userName# AND yw_type='账单分期' AND gd_type='账单分期工单' AND Rw_Status_sn='A' AND a.rw_ctime >#beginTime# AND a.rw_ctime <=#endTime#; 


	where  user_name = #userName# AND yw_type='大额EPPC' AND gd_type='大额EPPC工单' AND Rw_Status_sn='A' AND a.rw_ctime >#beginTime# AND a.rw_ctime <=#endTime#; 


	LEFT JOIN mdx_eppc b ON a.dr_key=b.dr_key where  a.user_name = #userName# AND a.yw_type='EPPC' AND a.gd_type='EPPC工单' AND a.Rw_Status_sn='A' AND a.rw_ctime >#beginTime# AND a.rw_ctime <=#endTime# AND b.Data_type!='新发卡备用金'; 


	LEFT JOIN mdx_eppc b ON a.dr_key=b.dr_key where  a.user_name = #userName# AND a.yw_type='EPPC' AND a.gd_type='EPPC工单' AND a.Rw_Status_sn='A' AND a.rw_ctime >#beginTime# AND a.rw_ctime <=#endTime# AND b.Data_type='新发卡备用金';
